mapper.select:
SELECT a.value FROM co_consumer_role AS cur INNER JOIN p_roles AS r ON r.id = cur.role_id INNER JOIN co_auth_role AS car ON car.role_id = cur.role_id INNER JOIN p_auths AS a ON a.id = car.auth_id
AND cur.consumer_id = #{consumerId}
AND cur.user_name = #{username}

SELECT a.value FROM co_admin_role AS cur INNER JOIN p_roles AS r ON r.id = cur.role_id INNER JOIN co_auth_role AS car ON car.role_id = cur.role_id INNER JOIN p_auths AS a ON a.id = car.auth_id
AND cur.admin_id = #{adminId}
AND cur.username = #{username}

SELECT id, username, phone, email, create_date, modify_date
FROM t_admins
WHERE id = #{adminId}
