mapper.select:
SELECT bf.*, f.id f_id, f.file_name f_file_name, f.json f_json, f.data_uri f_data_uri, f.width f_width, f.height f_height, f.bg_color f_bg_color, f.grid_size f_grid_size, f.draw_grid f_draw_grid, f.router_default f_router_default, f.connector_default f_connector_default, f.is_public f_is_public, f.is_legal f_is_legal, f.is_share f_is_share, f.create_date f_create_date, f.modify_date f_modify_date, f.dead_share_date f_dead_share_date, f.consumer_id f_consumer_id FROM t_blueprint_flowcharts bf INNER JOIN t_flowcharts f ON f.id = bf.flowchart_id
AND bf.scene = #{scene}
AND bf.price = #{price}
AND bf.ranking = #{ranking}
AND f.file_name LIKE CONCAT('%', #{fileName}, '%')

SELECT avatar, username, id
FROM t_consumers
WHERE id = #{consumerId};

mapper.update:
UPDATE t_blueprint_flowcharts
views = #{views}, copies = #{copies}, stars = #{stars}, tags = #{tags}, scene = #{scene}, price = #{price}, ranking = #{ranking}, `description` = #{description},
WHERE id = #{id}
